<div class="card">
  <div class="card-header">
    <span class="ms-1 fw-bold">Neuer Kunde</span>
  </div>

  <div class="card-body">
    <!--
          Template-Syntax:
              [formGroup]="createForm"  fuer Input (= Property Binding)
                                        d.h. formGroup fuer Attr. createForm
              (submit)="...()"  fuer Output (= Event Binding)
                                d.h. Ereignis submit an ...() anbinden
              Definition von Attributnamen gemaess HTML: Attribute names
              must consist of one or more characters other than the
              space characters, U+0000 NULL, """, "'", ">", "/", "=",
              the control characters, and any characters that are not
              defined by Unicode.
              https://github.com/angular/angular/issues/6786
      -->
    <!-- dynamisches Formular mit Validierung durch formGroup -->
    <!-- https://angular.io/guide/reactive-forms -->
    <form [formGroup]="createForm" role="form" (submit)="onSubmit()">
      <div class="row has-warning mb-4" *ngIf="showWarning">
        <div class="col col-12 form-control-feedback">
          <span class="alert alert-warning">
            <i class="material-icons">warning</i>
            <strong class="text-help ms-1">
              Wenn Sie die Seite jetzt verlassen, verlieren Sie Ihre
              eingegebenen Daten.
            </strong>
          </span>
        </div>
      </div>

      <hs-create-nachname [createForm]="createForm"></hs-create-nachname>
      <hs-create-email [createForm]="createForm"></hs-create-email>
      <hs-create-geburtsdatum
        [createForm]="createForm"
      ></hs-create-geburtsdatum>
      <hs-create-plz [createForm]="createForm"></hs-create-plz>
      <hs-create-ort [createForm]="createForm"></hs-create-ort>
      <hs-create-kategorie [createForm]="createForm"></hs-create-kategorie>
      <hs-create-newsletter [createForm]="createForm"></hs-create-newsletter>
      <hs-create-geschlecht [createForm]="createForm"></hs-create-geschlecht>
      <hs-create-familienstand
        [createForm]="createForm"
      ></hs-create-familienstand>
      <hs-create-interessen [createForm]="createForm"></hs-create-interessen>
      <div class="row">
        <div class="col offset-2 gy-3">
          <!-- [disabled]="..." fuer Input (= Property Binding) -->
          <button
            type="submit"
            class="btn btn-primary"
            [disabled]="createForm.invalid"
          >
            <i class="material-icons">check</i>
            <span class="ms-1">Jetzt anlegen</span>
          </button>
        </div>
      </div>
    </form>

    <small>* sind Pflichtfelder</small>
  </div>
</div>

<div class="mt-3">
  <hs-error-message [text]="errorMsg"></hs-error-message>
</div>
